mapper.select:
SELECT *
  FROM FILE
 WHERE PARENT_TABLE = #{parentTable}
   AND PARENT_NO = #{parentNo}
 ORDER BY REG_DATE DESC

mapper.update:
UPDATE FILE
   SET PARENT_TABLE = #{parentTable}
     , PARENT_NO   = #{parentNo}
     , FILE_NAME   = #{fileName}
     , ORIGIN_NAME = #{originName}
     , FILE_PATH   = #{filePath}
     , FILE_SIZE   = #{fileSize}
     , FILE_CODE   = #{fileCode}
WHERE FILE_NO = #{fileNo}

mapper.delete:
DELETE FROM FILE
 WHERE PARENT_TABLE = #{parentTable}
   AND PARENT_NO = #{parentNo}
